def calculate_average_velocity(final_position: float, initial_position: float, time_interval: float) -> float:
    if time_interval == 0:
        return float('inf')
    return (final_position - initial_position) / time_interval

def calculate_average_speed(total_distance: float, total_time: float) -> float:
    if total_time == 0:
        return float('inf')
    return total_distance / total_time

def calculate_average_acceleration(final_velocity: float, initial_velocity: float, time_interval: float) -> float:
    if time_interval == 0:
        return float('inf')
    return (final_velocity - initial_velocity) / time_interval

import math

def calculate_final_velocity_from_displacement(initial_velocity: float, acceleration: float, displacement: float) -> float:
    value = initial_velocity**2 + 2 * acceleration * displacement
    if value < 0:
        return float('nan')  # Represents an imaginary velocity
    return math.sqrt(value)

import math

def calculate_free_fall_velocity_from_displacement(displacement: float, gravity: float = 9.81) -> float:
    value = -2 * gravity * displacement
    if value < 0:
        return float('nan')
    return math.sqrt(value)
