import math

def calculate_projectile_time_of_flight(initial_speed: float, launch_angle_degrees: float, gravity: float = 9.81) -> float:
    if gravity == 0:
        return float('inf')
    launch_angle_radians = math.radians(launch_angle_degrees)
    return (2 * initial_speed * math.sin(launch_angle_radians)) / gravity

