def calculate_projectile_trajectory_y(x_position: float, initial_speed: float, launch_angle_degrees: float, gravity: float = 9.81) -> float:
    launch_angle_radians = math.radians(launch_angle_degrees)
    tan_theta = math.tan(launch_angle_radians)
    cos_theta = math.cos(launch_angle_radians)
    if initial_speed == 0 or cos_theta == 0:
        return float('nan')
    term1 = x_position * tan_theta
    term2 = (gravity * x_position**2) / (2 * initial_speed**2 * cos_theta**2)
    return term1 - term2

