def calculate_projectile_range_up_incline(initial_speed: float, projection_angle_alpha_degrees: float, incline_angle_beta_degrees: float, gravity: float = 9.81) -> float:
    alpha_rad = math.radians(projection_angle_alpha_degrees)
    beta_rad = math.radians(incline_angle_beta_degrees)
    cos_beta = math.cos(beta_rad)
    if gravity == 0 or cos_beta == 0:
        return float('inf')
    numerator = 2 * initial_speed**2 * math.sin(alpha_rad) * math.cos(alpha_rad + beta_rad)
    denominator = gravity * cos_beta**2
    return numerator / denominator

