def calculate_moment_of_inertia_discrete(masses: list[float], radii: list[float]) -> float:
    if len(masses) != len(radii):
        raise ValueError("Masses and radii lists must have the same length.")
    return sum(m * r**2 for m, r in zip(masses, radii))

import math

def calculate_radius_of_gyration(moment_of_inertia: float, mass: float) -> float:
    if mass == 0:
        return float('inf')
    return math.sqrt(moment_of_inertia / mass)

