import math

def calculate_river_crossing_net_speed_shortest_time(velocity_swimmer_relative_river: float, velocity_river: float) -> float:
    return math.sqrt(velocity_swimmer_relative_river**2 + velocity_river**2)

import math

def calculate_river_crossing_net_speed_shortest_path(velocity_swimmer_relative_river: float, velocity_river: float) -> float:
    if velocity_swimmer_relative_river < velocity_river:
        return float('nan')  # Cannot travel straight across
    return math.sqrt(velocity_swimmer_relative_river**2 - velocity_river**2)

import math

def calculate_river_crossing_time_shortest_path(river_width: float, velocity_swimmer_relative_river: float, velocity_river: float) -> float:
    if velocity_swimmer_relative_river < velocity_river:
        return float('inf')  # Cannot travel straight across
    net_speed_across = math.sqrt(velocity_swimmer_relative_river**2 - velocity_river**2)
    if net_speed_across == 0:
        return float('inf')
    return river_width / net_speed_across

import math

def calculate_river_crossing_angle_shortest_path(velocity_swimmer_relative_river: float, velocity_river: float) -> float:
    if velocity_swimmer_relative_river == 0 or velocity_swimmer_relative_river < velocity_river:
        return float('nan')  # Cannot travel straight across
    return math.degrees(math.asin(velocity_river / velocity_swimmer_relative_river))

import math

def calculate_relative_speed_rain_problem_perpendicular(velocity_rain: float, velocity_man: float) -> float:
    return math.sqrt(velocity_rain**2 + velocity_man**2)
