def calculate_new_spring_constant_from_length(original_spring_constant: float, original_length: float, new_length: float) -> float:
    if new_length == 0:
        return float('inf')  # Or raise an error
    return (original_spring_constant * original_length) / new_length

def calculate_springs_in_series(spring_constants: list[float]) -> float:
    if not spring_constants:
        return 0.0
    inverse_sum = sum(1 / k for k in spring_constants if k != 0)
    if inverse_sum == 0:
        return 0.0
    return 1 / inverse_sum

def calculate_atwood_machine_acceleration(mass_1: float, mass_2: float, gravity: float = 9.81) -> float:
    if mass_1 + mass_2 == 0:
        return 0.0
    return (mass_2 - mass_1) * gravity / (mass_1 + mass_2)

def calculate_atwood_machine_tension(mass_1: float, mass_2: float, gravity: float = 9.81) -> float:
    if mass_1 + mass_2 == 0:
        return 0.0
    return (2 * mass_1 * mass_2 * gravity) / (mass_1 + mass_2)
