import math

def calculate_work_done_constant_force(force_magnitude: float, displacement_magnitude: float, angle_degrees: float) -> float:
    angle_radians = math.radians(angle_degrees)
    return force_magnitude * displacement_magnitude * math.cos(angle_radians)

def calculate_kinetic_energy_from_momentum(momentum: float, mass: float) -> float:
    if mass == 0:
        return float('inf')
    return (momentum**2) / (2 * mass)

import math

def calculate_momentum_from_kinetic_energy(kinetic_energy: float, mass: float) -> float:
    if kinetic_energy < 0 or mass < 0:
        return float('nan')
    return math.sqrt(2 * mass * kinetic_energy)

def calculate_average_power(work: float, time: float) -> float:
    if time == 0:
        return float('inf')
    return work / time

import math

def calculate_instantaneous_power(force_magnitude: float, velocity_magnitude: float, angle_degrees: float) -> float:
    angle_radians = math.radians(angle_degrees)
    return force_magnitude * velocity_magnitude * math.cos(angle_radians)
