def calculate_center_of_mass_1d(masses: list[float], positions: list[float]) -> float:
    if len(masses) != len(positions):
        raise ValueError("Masses and positions lists must have the same length.")
    total_mass = sum(masses)
    if total_mass == 0:
        return 0.0
    mass_moment = sum(m * p for m, p in zip(masses, positions))
    return mass_moment / total_mass

def calculate_com_distances_two_masses(mass_1: float, mass_2: float, total_distance: float) -> tuple[float, float]:
    total_mass = mass_1 + mass_2
    if total_mass == 0:
        return (0.0, 0.0)
    distance_from_m1 = (mass_2 * total_distance) / total_mass
    distance_from_m2 = (mass_1 * total_distance) / total_mass
    return (distance_from_m1, distance_from_m2)

