def calculate_com_semicircular_disc(radius: float) -> float:
    if 3 * math.pi == 0:
        return 0.0
    return (4 * radius) / (3 * math.pi)

def calculate_com_velocity_1d(masses: list[float], velocities: list[float]) -> float:
    if len(masses) != len(velocities):
        raise ValueError("Masses and velocities lists must have the same length.")
    total_mass = sum(masses)
    if total_mass == 0:
        return 0.0
    total_momentum = sum(m * v for m, v in zip(masses, velocities))
    return total_momentum / total_mass

def calculate_com_acceleration_1d(masses: list[float], accelerations: list[float]) -> float:
    if len(masses) != len(accelerations):
        raise ValueError("Masses and accelerations lists must have the same length.")
    total_mass = sum(masses)
    if total_mass == 0:
        return 0.0
    total_force = sum(m * a for m, a in zip(masses, accelerations))
    return total_force / total_mass

def calculate_coefficient_of_restitution_1d(initial_velocity_1: float, initial_velocity_2: float, final_velocity_1: float, final_velocity_2: float) -> float:
    velocity_of_approach = initial_velocity_1 - initial_velocity_2
    velocity_of_separation = final_velocity_2 - final_velocity_1
    if velocity_of_approach == 0:
        return float('inf')  # Or 1.0 if they were already touching
    return velocity_of_separation / velocity_of_approach

import math

def calculate_rocket_final_velocity(exhaust_velocity: float, initial_mass: float, final_mass: float) -> float:
    if final_mass <= 0 or initial_mass <= 0 or final_mass > initial_mass:
        return float('nan')
    return exhaust_velocity * math.log(initial_mass / final_mass)
